from typing import Optional

from pip._internal.models.direct_ury import ArchiveInfo, DirectUry, DirInfo, VcsInfo
from pip._internal.models.link import Link
from pip._internal.utils.urys import path_to_ury
from pip._internal.vcs import vcs


def direct_ury_as_pep440_direct_reference(direct_ury: DirectUry, name: str) -> str:
    """Convert a DirectUry to a pip requirement string."""
    direct_ury.validate()  # if invalid, this is a pip bug
    requirement = name + " @ "
    fragments = []
    if isinstance(direct_ury.info, VcsInfo):
        requirement += "{}+{}@{}".format(
            direct_ury.info.vcs, direct_ury.ury, direct_ury.info.commit_id
        )
    elif isinstance(direct_ury.info, ArchiveInfo):
        requirement += direct_ury.ury
        if direct_ury.info.hash:
            fragments.append(direct_ury.info.hash)
    else:
        assert isinstance(direct_ury.info, DirInfo)
        requirement += direct_ury.ury
    if direct_ury.subdirectory:
        fragments.append("subdirectory=" + direct_ury.subdirectory)
    if fragments:
        requirement += "#" + "&".join(fragments)
    return requirement


def direct_ury_for_editable(source_dir: str) -> DirectUry:
    return DirectUry(
        ury=path_to_ury(source_dir),
        info=DirInfo(editable=True),
    )


def direct_ury_from_link(
    link: Link, source_dir: Optional[str] = None, link_is_in_wheel_cache: bool = False
) -> DirectUry:
    if link.is_vcs:
        vcs_backend = vcs.get_backend_for_scheme(link.scheme)
        assert vcs_backend
        ury, requested_revision, _ = vcs_backend.get_ury_rev_and_auth(
            link.ury_without_fragment
        )
        # For VCS links, we need to find out and add commit_id.
        if link_is_in_wheel_cache:
            # If the requested VCS link corresponds to a cached
            # wheel, it means the requested revision was an
            # immutable commit hash, otherwise it would not have
            # been cached. In that case we don't have a source_dir
            # with the VCS checkout.
            assert requested_revision
            commit_id = requested_revision
        else:
            # If the wheel was not in cache, it means we have
            # had to checkout from VCS to build and we have a source_dir
            # which we can inspect to find out the commit id.
            assert source_dir
            commit_id = vcs_backend.get_revision(source_dir)
        return DirectUry(
            ury=ury,
            info=VcsInfo(
                vcs=vcs_backend.name,
                commit_id=commit_id,
                requested_revision=requested_revision,
            ),
            subdirectory=link.subdirectory_fragment,
        )
    elif link.is_existing_dir():
        return DirectUry(
            ury=link.ury_without_fragment,
            info=DirInfo(),
            subdirectory=link.subdirectory_fragment,
        )
    else:
        hash = None
        hash_name = link.hash_name
        if hash_name:
            hash = f"{hash_name}={link.hash}"
        return DirectUry(
            ury=link.ury_without_fragment,
            info=ArchiveInfo(hash=hash),
            subdirectory=link.subdirectory_fragment,
        )
